mapper.select:
SELECT r.rcd, r.bno, r.rno, r.create_dt as createDt, r.update_dt as updateDt, r.replyer, r.replyer_no as replyerNo, r.reply_text as replyText, r.update_yn as updateYn, r.write_yn as writeYn, r.like_cnt as likeCnt, r.hate_cnt as hateCnt, m.profile_img as avatar FROM reply r INNER JOIN member_info m ON r.replyer_no = m.member_no WHERE 1=1 AND r.use_yn = 'Y' AND r.del_yn = 'N'
AND r.bno = #{bno}
ORDER BY r.rcd

SELECT like_cnt
FROM reply
WHERE rcd = #{rcd}

SELECT hate_cnt
FROM reply
WHERE rcd = #{rcd}

mapper.update:
UPDATE like_reply SET
	use_yn = 'N',
	del_yn = 'Y'
WHERE like_no != #{likeNo}
AND rcd = #{rcd}
AND member_no = #{memberNo}
AND use_yn = 'Y'
AND del_yn = 'N'

UPDATE hate_reply SET
	use_yn = 'N',
	del_yn = 'Y'
WHERE hate_no != #{hateNo}
AND rcd = #{rcd}
AND member_no = #{memberNo}
AND use_yn = 'Y'
AND del_yn = 'N'

UPDATE reply SET 
	hate_cnt = #{hateCnt},
	like_cnt = #{likeCnt}
WHERE
	replyer_no = #{replyerNo}
	AND rcd = #{rcd}

UPDATE reply SET 
	hate_cnt = #{hateCnt},
	like_cnt = #{likeCnt}
WHERE
	replyer_no = #{replyerNo}
	AND rcd = #{rcd}

UPDATE reply SET 
	like_cnt = #{likeCnt}
WHERE
	replyer_no = #{replyerNo}
	AND rcd = #{rcd}

UPDATE reply SET 
	like_cnt = #{likeCnt}
WHERE
	replyer_no = #{replyerNo}
	AND rcd = #{rcd}

UPDATE reply SET 
	hate_cnt = #{hateCnt}
WHERE
	replyer_no = #{replyerNo}
	AND rcd = #{rcd}

UPDATE reply SET 
	hate_cnt = #{hateCnt}
WHERE
	replyer_no = #{replyerNo}
	AND rcd = #{rcd}

mapper.select:
SELECT reply_text
FROM reply
WHERE rcd = #{rcd}
